function color(node, params) {
    const apply = ({ hue, sat, lum }) => {
        node.style.setProperty('--hue', hue)
        node.style.setProperty('--sat', sat + '%')
        node.style.setProperty('--lum', lum + '%')
    }

    apply(params)

    return {
        update: apply
    }
}

export default color
