<script>
	import Button from './Button.svelte'
	export let currentIndex
    export let pages = []
	export let setIndex
</script>

<nav>
	{#each pages as page, i}
		<Button 
			hsl={page}
			active={i === currentIndex} 
			on:click={() => setIndex(i)}>
			{i+1}
		</Button>
	{/each}
</nav>

<style>
    nav {
		display: flex;
		flex-direction: column;
		gap: .25rem;
    }
</style>